    }
  }

}
//路由守卫
router.beforeEach((to, from, next) => {
  localStorage.setItem("currentPathName", to.name)  //设置当前的路由名称,为了在Header组件中使用
  store.commit("setPath") //触发shore的数据更新
  next()  //放行路由
})
export default router
